import React from 'react';

// Modal de paiement compl√®te et corrig√©e
const PaymentModalComplete = ({ 
  paymentModal, 
  setPaymentModal, 
  clients, 
  payments, 
  setPayments, 
  invoices, 
  setInvoices, 
  saveToStorage 
}) => {
  const handlePaymentMethodSelect = (method) => {
    markPaymentReceived(
      paymentModal.clientId,
      paymentModal.paymentNumber,
      paymentModal.amount,
      new Date().toISOString().split('T')[0],
      method
    );
    setPaymentModal({ isOpen: false, clientId: null, paymentNumber: null, amount: 0 });
  };

  const markPaymentReceived = (clientId, paymentNumber, amount, date, paymentMethod) => {
    const payment = {
      id: Date.now(),
      clientId: parseInt(clientId),
      paymentNumber: paymentNumber,
      amount: parseFloat(amount),
      date: date,
      paymentMethod: paymentMethod,
      received: true,
      recordedAt: new Date().toISOString()
    };

    const newPayments = [...payments, payment];
    setPayments(newPayments);
    saveToStorage('payments', newPayments);

    // Cr√©er automatiquement une facture de revenu
    const client = clients.find(c => c.id === parseInt(clientId));
    if (client) {
      const invoice = {
        id: Date.now() + 1,
        clientId: parseInt(clientId),
        amount: parseFloat(amount),
        date: date,
        type: 'revenu',
        description: `Paiement ${paymentNumber}${paymentNumber === 1 ? 'er' : 'e'} versement - ${client.name} (${paymentMethod === 'cheque' ? 'Ch√®que' : 'Comptant'})`
      };

      const newInvoices = [...invoices, invoice];
      setInvoices(newInvoices);
      saveToStorage('invoices', newInvoices);
    }

    alert(`Paiement ${paymentNumber}${paymentNumber === 1 ? 'er' : 'e'} versement marqu√© comme re√ßu (${paymentMethod === 'cheque' ? 'Ch√®que' : 'Comptant'}) !`);
  };

  const closePaymentModal = () => {
    setPaymentModal({ isOpen: false, clientId: null, paymentNumber: null, amount: 0 });
  };

  if (!paymentModal.isOpen) return null;

  return (
    <div style={{
      position: 'fixed', 
      top: 0, 
      left: 0, 
      width: '100%', 
      height: '100%',
      background: 'rgba(0,0,0,0.5)', 
      zIndex: 1000,
      display: 'flex', 
      alignItems: 'center', 
      justifyContent: 'center',
      padding: '20px'
    }}>
      <div style={{
        background: 'white', 
        borderRadius: '12px', 
        padding: '25px',
        width: '100%', 
        maxWidth: '450px',
        boxShadow: '0 10px 30px rgba(0,0,0,0.3)'
      }}>
        <div style={{ textAlign: 'center', marginBottom: '20px' }}>
          <h3 style={{ 
            color: '#1a4d1a', 
            marginBottom: '8px', 
            fontSize: '1.3em',
            fontWeight: 'bold'
          }}>
            Confirmer le Paiement
          </h3>
          <p style={{ 
            color: '#666', 
            fontSize: '16px',
            margin: 0
          }}>
            Montant: <strong style={{ color: '#28a745' }}>{paymentModal.amount.toFixed(2)}$</strong>
          </p>
        </div>

        <div style={{ marginBottom: '15px' }}>
          <p style={{ 
            color: '#666', 
            marginBottom: '15px', 
            fontSize: '14px',
            textAlign: 'center'
          }}>
            Comment le paiement a-t-il √©t√© re√ßu ?
          </p>
        </div>

        <div style={{ 
          display: 'grid', 
          gap: '12px', 
          marginBottom: '20px' 
        }}>
          <button
            onClick={() => handlePaymentMethodSelect('cheque')}
            style={{
              padding: '18px 20px', 
              border: '2px solid #007bff',
              borderRadius: '10px', 
              background: 'white', 
              cursor: 'pointer',
              fontSize: '16px', 
              fontWeight: 'bold', 
              color: '#007bff',
              transition: 'all 0.2s ease',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              gap: '8px'
            }}
            onMouseEnter={(e) => {
              e.target.style.background = '#007bff';
              e.target.style.color = 'white';
            }}
            onMouseLeave={(e) => {
              e.target.style.background = 'white';
              e.target.style.color = '#007bff';
            }}
          >
            üìÑ Paiement par Ch√®que
          </button>

          <button
            onClick={() => handlePaymentMethodSelect('comptant')}
            style={{
              padding: '18px 20px', 
              border: '2px solid #28a745',
              borderRadius: '10px', 
              background: 'white', 
              cursor: 'pointer',
              fontSize: '16px', 
              fontWeight: 'bold', 
              color: '#28a745',
              transition: 'all 0.2s ease',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              gap: '8px'
            }}
            onMouseEnter={(e) => {
              e.target.style.background = '#28a745';
              e.target.style.color = 'white';
            }}
            onMouseLeave={(e) => {
              e.target.style.background = 'white';
              e.target.style.color = '#28a745';
            }}
          >
            üí∞ Argent Comptant
          </button>
        </div>

        <button
          onClick={closePaymentModal}
          style={{
            padding: '12px 20px', 
            border: 'none',
            background: '#6c757d', 
            color: 'white', 
            borderRadius: '8px',
            cursor: 'pointer', 
            width: '100%', 
            fontSize: '14px',
            fontWeight: 'bold',
            transition: 'all 0.2s ease'
          }}
          onMouseEnter={(e) => {
            e.target.style.background = '#5a6268';
          }}
          onMouseLeave={(e) => {
            e.target.style.background = '#6c757d';
          }}
        >
          Annuler
        </button>
      </div>
    </div>
  );
};

// Fonction pour afficher la modal de paiement
const showPaymentModal = (clientId, paymentNumber, amount, setPaymentModal) => {
  setPaymentModal({
    isOpen: true,
    clientId,
    paymentNumber,
    amount
  });
};

// Application principale simplifi√©e avec modal corrig√©e
const AppWithFixedModal = () => {
  const [paymentModal, setPaymentModal] = React.useState({
    isOpen: false,
    clientId: null,
    paymentNumber: null,
    amount: 0
  });

  const [clients] = React.useState([
    { id: 1, name: 'Jean Dupont', address: '123 Rue Principale, Mirabel' },
    { id: 2, name: 'Marie Martin', address: '456 Avenue des √ârables, Mirabel' }
  ]);

  const [payments, setPayments] = React.useState([]);
  const [invoices, setInvoices] = React.useState([]);

  // Fonction de sauvegarde simul√©e
  const saveToStorage = (key, data) => {
    try {
      localStorage.setItem(key, JSON.stringify(data));
      console.log(`Sauvegard√©: ${key}`);
    } catch (error) {
      console.error('Erreur sauvegarde:', error);
    }
  };

  return (
    <div style={{ 
      fontFamily: 'Arial, sans-serif', 
      padding: '40px', 
      background: '#f5f5f5',
      minHeight: '100vh'
    }}>
      <div style={{ 
        maxWidth: '800px', 
        margin: '0 auto',
        background: 'white',
        padding: '30px',
        borderRadius: '15px',
        boxShadow: '0 4px 15px rgba(0,0,0,0.1)'
      }}>
        <h1 style={{ 
          color: '#1a4d1a', 
          marginBottom: '30px',
          textAlign: 'center'
        }}>
          Test Modal de Paiement - JM Pominville
        </h1>

        <div style={{ 
          display: 'grid', 
          gap: '20px',
          marginBottom: '30px'
        }}>
          <h2 style={{ color: '#495057' }}>Clients avec contrats:</h2>

          {clients.map(client => (
            <div key={client.id} style={{
              border: '1px solid #ddd',
              borderRadius: '8px',
              padding: '20px',
              background: '#f8f9fa'
            }}>
              <h3 style={{ margin: '0 0 10px 0', color: '#1a4d1a' }}>
                {client.name}
              </h3>
              <p style={{ margin: '0 0 15px 0', color: '#666' }}>
                {client.address}
              </p>

              <div style={{ display: 'flex', gap: '10px' }}>
                <button
                  onClick={() => showPaymentModal(client.id, 1, 350, setPaymentModal)}
                  style={{
                    padding: '10px 15px',
                    background: '#28a745',
                    color: 'white',
                    border: 'none',
                    borderRadius: '6px',
                    cursor: 'pointer',
                    fontWeight: 'bold'
                  }}
                >
                  1er Paiement (350$)
                </button>

                <button
                  onClick={() => showPaymentModal(client.id, 2, 350, setPaymentModal)}
                  style={{
                    padding: '10px 15px',
                    background: '#007bff',
                    color: 'white',
                    border: 'none',
                    borderRadius: '6px',
                    cursor: 'pointer',
                    fontWeight: 'bold'
                  }}
                >
                  2e Paiement (350$)
                </button>
              </div>
            </div>
          ))}
        </div>

        {/* Historique des paiements */}
        <div>
          <h2 style={{ color: '#495057', marginBottom: '15px' }}>
            Paiements enregistr√©s ({payments.length}):
          </h2>

          {payments.length > 0 ? (
            <div style={{ 
              background: '#f8f9fa', 
              padding: '20px', 
              borderRadius: '8px' 
            }}>
              {payments.map(payment => {
                const client = clients.find(c => c.id === payment.clientId);
                return (
                  <div key={payment.id} style={{
                    padding: '10px 0',
                    borderBottom: '1px solid #ddd'
                  }}>
                    <strong>{client?.name}</strong> - 
                    {payment.paymentNumber}{payment.paymentNumber === 1 ? 'er' : 'e'} versement - 
                    {payment.amount}$ ({payment.paymentMethod === 'cheque' ? 'Ch√®que' : 'Comptant'}) - 
                    {payment.date}
                  </div>
                );
              })}
            </div>
          ) : (
            <p style={{ 
              color: '#666',
              fontStyle: 'italic',
              textAlign: 'center',
              padding: '20px'
            }}>
              Aucun paiement enregistr√© pour le moment.
            </p>
          )}
        </div>
      </div>

      {/* Modal de paiement */}
      <PaymentModalComplete
        paymentModal={paymentModal}
        setPaymentModal={setPaymentModal}
        clients={clients}
        payments={payments}
        setPayments={setPayments}
        invoices={invoices}
        setInvoices={setInvoices}
        saveToStorage={saveToStorage}
      />
    </div>
  );
};

export default PaymentModalComplete;
