      }

    } catch (error) {
      console.error('Erreur complète shareLocationWithClients:', error);
      setGpsError(error.message);
      alert('Erreur lors du partage de localisation: ' + error.message);
    }
  };

  const startLocationTracking = async () => {
    if (!gpsPosition) {
