import React, {useState} from 'react';
// Section Notifications - Version simplifi√©e pour web
const NotificationsSection = ({ clients, backendConnected = false }) => {
  const [selectedClients, setSelectedClients] = useState([]);
  const [messageType, setMessageType] = useState('enroute');
  const [customMessage, setCustomMessage] = useState('');

  // Fonction pour extraire le nom de rue (simplifi√©e)
  const extractStreetName = (address) => {
    if (!address) return 'Adresse non d√©finie';

    try {
      const parts = address.split(',');
      let streetPart = parts[0].trim();

      // Enlever le num√©ro de rue
      streetPart = streetPart.replace(/^(\d+\s*[-\/\s]?\s*[a-zA-Z]?\s*)/i, '').trim();

      if (!streetPart) streetPart = parts[0].trim();

      // Normaliser les types de rues
      streetPart = streetPart
        .replace(/^(rue\s+)/gi, 'Rue ')
        .replace(/^(av\.?\s+)/gi, 'Avenue ')
        .replace(/^(boul\.?\s+)/gi, 'Boulevard ');

      return streetPart || 'Adresse non d√©finie';
    } catch (error) {
      return 'Adresse non d√©finie';
    }
  };

  // Grouper les clients par rue
  const groupClientsByStreet = (clients) => {
    const streetGroups = {};

    clients.forEach(client => {
      const streetName = extractStreetName(client.address);
      if (!streetGroups[streetName]) {
        streetGroups[streetName] = [];
      }
      streetGroups[streetName].push(client);
    });

    return streetGroups;
  };

  const sendDemoNotification = (clientId, type) => {
    const client = clients.find(c => c.id === clientId);
    if (!client) {
      alert('Client introuvable');
      return;
    }

    let message = '';
    switch (type) {
      case 'enroute':
        message = `üöõ Notre √©quipe se dirige vers votre secteur. ETA: 15-30 minutes.`;
        break;
      case 'completion':
        message = `‚úÖ Service de d√©neigement termin√© √† votre adresse. Merci!`;
        break;
      case 'reminder':
        message = `üí∞ Rappel amical: votre paiement de d√©neigement est d√ª.`;
        break;
      default:
        message = customMessage || 'Message de JM Pominville';
    }

    alert(`üì± NOTIFICATION D√âMO envoy√©e √† ${client.name}:\n\n"${message}"\n\n(Version web - notifications simul√©es)`);
  };

  const sendBulkNotifications = () => {
    if (selectedClients.length === 0) {
      alert('Veuillez s√©lectionner au moins un client');
      return;
    }

    const confirmMessage = `Envoyer des notifications "${messageType}" √† ${selectedClients.length} client(s) s√©lectionn√©(s) ?`;

    if (!window.confirm(confirmMessage)) return;

    selectedClients.forEach((clientId, index) => {
      setTimeout(() => {
        sendDemoNotification(clientId, messageType);
      }, index * 1000); // D√©lai d'1 seconde entre chaque notification
    });

    setSelectedClients([]);
  };

  const streetGroups = groupClientsByStreet(clients);

  return (
    <div style={{ background: 'white', padding: '30px', borderRadius: '15px', boxShadow: '0 4px 15px rgba(0,0,0,0.1)' }}>
      <h2 style={{ color: '#1a4d1a', marginBottom: '25px', fontSize: '1.8em' }}>üì± Notifications</h2>

      {/* Statut de connexion */}
      <div style={{
        background: backendConnected ? '#d4edda' : '#f8d7da',
        padding: '15px', borderRadius: '10px',
        marginBottom: '20px', textAlign: 'center', fontWeight: 'bold',
        color: backendConnected ? '#155724' : '#721c24'
      }}>
        {backendConnected ? 'üü¢ Connect√© - Notifications r√©elles' : 'üî¥ Mode Web - Notifications simul√©es'}
      </div>

      {/* Configuration des notifications */}
      <div style={{
        background: '#f8f9fa', padding: '20px', borderRadius: '12px',
        marginBottom: '20px', border: '1px solid #ddd'
      }}>
        <h4 style={{ marginBottom: '15px', color: '#495057' }}>Configuration des notifications</h4>

        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))', gap: '15px' }}>
          <div>
            <label style={{ display: 'block', marginBottom: '5px', fontWeight: 'bold' }}>
              Type de notification:
            </label>
            <select 
              value={messageType} 
              onChange={(e) => setMessageType(e.target.value)}
              style={{ width: '100%', padding: '10px', borderRadius: '6px', border: '1px solid #ddd' }}
            >
              <option value="enroute">üöõ En route</option>
              <option value="completion">‚úÖ Service termin√©</option>
              <option value="reminder">üí∞ Rappel paiement</option>
              <option value="custom">‚úèÔ∏è Message personnalis√©</option>
            </select>
          </div>

          <div>
            <label style={{ display: 'block', marginBottom: '5px', fontWeight: 'bold' }}>
              Message personnalis√©:
            </label>
            <textarea 
              value={customMessage}
              onChange={(e) => setCustomMessage(e.target.value)}
              placeholder={messageType === 'custom' ? 'Saisissez votre message...' : 'Message optionnel'}
              style={{
                width: '100%', padding: '10px', borderRadius: '6px', 
                border: '1px solid #ddd', minHeight: '60px', resize: 'vertical'
              }}
            />
          </div>
        </div>
      </div>

      {/* S√©lection par rue */}
      <div style={{
        background: '#e8f5e8', padding: '20px', borderRadius: '12px',
        marginBottom: '20px', border: '1px solid #c3e6cb'
      }}>
        <h4 style={{ marginBottom: '15px', color: '#155724' }}>üó∫Ô∏è Notifications par rue</h4>

        {Object.keys(streetGroups).length > 0 ? (
          <div style={{ display: 'grid', gap: '10px' }}>
            {Object.entries(streetGroups)
              .sort(([a], [b]) => a.localeCompare(b))
              .map(([street, streetClients]) => (
                <div key={street} style={{
                  background: 'white', padding: '15px', borderRadius: '8px',
                  border: '1px solid #c3e6cb'
                }}>
                  <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '10px' }}>
                    <div>
                      <strong style={{ color: '#155724' }}>üìç {street}</strong>
                      <div style={{ fontSize: '14px', color: '#666' }}>
                        {streetClients.length} client{streetClients.length !== 1 ? 's' : ''}
                      </div>
                    </div>
                    <button
                      onClick={() => {
                        const clientIds = streetClients.map(c => c.id);
                        streetClients.forEach(client => {
                          setTimeout(() => {
                            sendDemoNotification(client.id, messageType);
                          }, Math.random() * 2000);
                        });
                      }}
                      style={{
                        padding: '8px 15px', background: '#28a745', color: 'white',
                        border: 'none', borderRadius: '6px', cursor: 'pointer',
                        fontWeight: 'bold', fontSize: '14px'
                      }}
                    >
                      üì§ Notifier la rue
                    </button>
                  </div>

                  {/* Liste des clients de cette rue */}
                  <div style={{ display: 'grid', gap: '5px' }}>
                    {streetClients.map(client => (
                      <div key={client.id} style={{
                        display: 'flex', justifyContent: 'space-between', alignItems: 'center',
                        padding: '8px 10px', background: '#f8f9fa', borderRadius: '6px'
                      }}>
                        <div>
                          <strong>{client.name}</strong>
                          <div style={{ fontSize: '12px', color: '#666' }}>
                            {client.phone} {client.email && `‚Ä¢ ${client.email}`}
                          </div>
                        </div>
                        <button
                          onClick={() => sendDemoNotification(client.id, messageType)}
                          style={{
                            padding: '5px 10px', background: '#007bff', color: 'white',
                            border: 'none', borderRadius: '4px', cursor: 'pointer',
                            fontSize: '12px', fontWeight: 'bold'
                          }}
                        >
                          üì± Notifier
                        </button>
                      </div>
                    ))}
                  </div>
                </div>
              ))}
          </div>
        ) : (
          <div style={{
            textAlign: 'center', padding: '40px 20px',
            color: '#666', fontSize: '16px'
          }}>
            Aucun client disponible pour les notifications.
          </div>
        )}
      </div>

      {/* Information importante */}
      <div style={{
        background: '#fff3cd', padding: '15px', borderRadius: '8px',
        border: '1px solid #ffeaa7', color: '#856404'
      }}>
        <strong>‚ÑπÔ∏è Mode D√©mo:</strong> Cette version web simule l'envoi de notifications. 
        Pour envoyer de vraies notifications SMS/Email, activez le backend Node.js.
      </div>
    </div>
  );
};
export default NotificationsSection;
