            <div style={{ display: 'flex', gap: '10px', marginBottom: '20px' }}>
              <button
                onClick={() => setShowArchived(false)}
                style={{
                  padding: '10px 15px',
                  background: !showArchived ? '#1a4d1a' : '#6c757d',
