import React, { useState } from 'react';

// Section Terrain - Version simplifiée pour Render
const TerrainSection = () => {
  const [gpsPosition, setGpsPosition] = useState(null);
  const [gpsError, setGpsError] = useState(null);
  const [isLocationShared, setIsLocationShared] = useState(false);

  const getCurrentPosition = async () => {
    setGpsError(null);

    if (!navigator.geolocation) {
      const error = 'Géolocalisation non supportée par ce navigateur';
      setGpsError(error);
      return;
    }

    try {
      const position = await new Promise((resolve, reject) => {
        const options = {
          enableHighAccuracy: false,
          timeout: 15000,
          maximumAge: 300000
        };

        navigator.geolocation.getCurrentPosition(
          (pos) => resolve(pos),
          (error) => {
            let errorMessage = 'Erreur de géolocalisation';
            switch (error.code) {
              case error.PERMISSION_DENIED:
                errorMessage = 'Permission de géolocalisation refusée. Autorisez l\'accès dans votre navigateur.';
                break;
              case error.POSITION_UNAVAILABLE:
                errorMessage = 'Position non disponible. Vérifiez que le GPS est activé.';
                break;
              case error.TIMEOUT:
                errorMessage = 'Délai d\'attente dépassé. Réessayez.';
                break;
              default:
                errorMessage = `Erreur GPS (${error.code}): ${error.message}`;
                break;
            }
            reject(new Error(errorMessage));
          },
          options
        );
      });

      const pos = {
        lat: position.coords.latitude,
        lng: position.coords.longitude,
        accuracy: Math.round(position.coords.accuracy || 1000),
        time: new Date().toLocaleTimeString('fr-CA'),
        timestamp: position.timestamp || Date.now(),
        method: 'web-gps'
      };

      setGpsPosition(pos);
      alert(`Position obtenue avec succès!\nPrécision: ${pos.accuracy}m`);

    } catch (error) {
      setGpsError(error.message);
    }
  };

  const shareLocationDemo = () => {
    if (!gpsPosition) {
      alert('Obtenez d\'abord votre position GPS');
      return;
    }

    setIsLocationShared(true);
    alert('Position partagée (fonction démo pour version web)');
  };

  return (
    <div style={{ background: 'white', padding: '30px', borderRadius: '15px', boxShadow: '0 4px 15px rgba(0,0,0,0.1)' }}>
      <h2 style={{ color: '#1a4d1a', marginBottom: '25px', fontSize: '1.8em' }}>🚛 Vue Équipe Terrain</h2>

      {/* Section GPS */}
      <div style={{
        background: '#f8f9fa', padding: '20px', borderRadius: '15px',
        marginBottom: '20px', border: isLocationShared ? '3px solid #28a745' : '1px solid #ddd'
      }}>
        <h3 style={{ margin: '0 0 15px 0', color: '#1a4d1a' }}>📍 Position GPS</h3>

        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: '10px' }}>
          <button 
            onClick={getCurrentPosition}
            style={{
              padding: '15px 20px', background: '#007bff', color: 'white',
              border: 'none', borderRadius: '8px', fontWeight: 'bold',
              fontSize: '16px', cursor: 'pointer'
            }}
          >
            📍 Obtenir Position GPS
          </button>

          {gpsPosition && (
            <button 
              onClick={shareLocationDemo}
              style={{
                padding: '15px 20px', background: '#28a745', color: 'white',
                border: 'none', borderRadius: '8px', fontWeight: 'bold',
                fontSize: '16px', cursor: 'pointer'
              }}
            >
              📤 Partager Position (Démo)
            </button>
          )}
        </div>

        {gpsPosition && (
          <div style={{
            background: '#d4edda', padding: '15px', borderRadius: '12px',
            border: '1px solid #c3e6cb', marginTop: '15px'
          }}>
            <h4 style={{ margin: '0 0 10px 0', color: '#155724' }}>Position Actuelle</h4>
            <div style={{ fontSize: '14px' }}>
              <strong>Coordonnées:</strong><br />
              <span>Latitude: {gpsPosition.lat.toFixed(6)}</span><br />
              <span>Longitude: {gpsPosition.lng.toFixed(6)}</span>
            </div>
            <div style={{ fontSize: '12px', color: '#666', marginTop: '8px' }}>
              Précision: {gpsPosition.accuracy}m | Heure: {gpsPosition.time}
            </div>
          </div>
        )}

        {gpsError && (
          <div style={{
            background: '#f8d7da', padding: '15px', borderRadius: '12px',
            border: '1px solid #f5c6cb', marginTop: '15px', color: '#721c24'
          }}>
            <strong>Erreur GPS:</strong> {gpsError}
          </div>
        )}
      </div>

      {/* Actions rapides */}
      <div style={{
        background: 'white', 
        padding: '20px', 
        borderRadius: '15px',
        boxShadow: '0 4px 15px rgba(0,0,0,0.1)', 
        marginBottom: '20px',
        border: '1px solid #ddd'
      }}>
        <h3 style={{ color: '#1a4d1a', marginBottom: '15px' }}>🚀 Actions Rapides</h3>

        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))', gap: '10px' }}>
          <button 
            onClick={() => alert('Fonction de notification groupée (version démo web)')}
            style={{
              padding: '15px 20px', background: '#17a2b8', color: 'white',
              border: 'none', borderRadius: '8px', fontWeight: 'bold',
              fontSize: '16px', cursor: 'pointer'
            }}
          >
            🌅 Notifier Début de Journée
          </button>

          <button 
            onClick={() => alert('Fonction de notification groupée (version démo web)')}
            style={{
              padding: '15px 20px', background: '#6f42c1', color: 'white',
              border: 'none', borderRadius: '8px', fontWeight: 'bold',
              fontSize: '16px', cursor: 'pointer'
            }}
          >
            🌙 Notifier Fin de Journée
          </button>

          <button 
            onClick={() => alert('Version Web Render - Backend désactivé')}
            style={{
              padding: '15px 20px', 
              background: '#dc3545', 
              color: 'white', border: 'none', borderRadius: '8px',
              fontWeight: 'bold', fontSize: '16px', cursor: 'pointer'
            }}
          >
            🔴 Mode Web Statique
          </button>
        </div>
      </div>

      {/* Information importante */}
      <div style={{
        background: '#fff3cd', padding: '20px', borderRadius: '12px',
        border: '1px solid #ffeaa7', color: '#856404'
      }}>
        <h4 style={{ margin: '0 0 10px 0' }}>ℹ️ Information Version Web</h4>
        <p style={{ margin: 0, lineHeight: '1.5' }}>
          Cette version web est optimisée pour Render. Les fonctionnalités GPS fonctionnent 
          localement, mais les notifications nécessiteraient un backend activé. Toutes les 
          données sont sauvegardées dans le stockage local du navigateur.
        </p>
      </div>
    </div>
  );
};
export default TerrainSection;
